package com.kevinfei.demo.controller;import com.kevinfei.demo.bean.BgResult;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;@RestController@RequestMapping("/books")public class BookController {    @GetMapping    public String getById() {        System.out.println("Spring Boot Running....");        return "Spring Boot Running....";    }    @RequestMapping("/getLists")    public BgResult getList() {        BgResult result = new BgResult();        result.setSuccess("ok");        result.setCode(200);        result.setMessage("响应成功");        result.setData(new ArrayList<>());        return new BgResult();    }}